<div class="highlight"><pre style="color:#f8f8f2;background-color:#272822;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#75715e">#include</span> <span style="color:#75715e">&lt;iostream&gt;</span><span style="color:#75715e">
</span><span style="color:#75715e"></span>
<span style="color:#66d9ef">typedef</span> <span style="color:#66d9ef">long</span> <span style="color:#66d9ef">long</span> i64;

<span style="color:#66d9ef">constexpr</span> i64 N <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> <span style="color:#ae81ff">19</span>, K <span style="color:#f92672">=</span> <span style="color:#ae81ff">19</span>, P <span style="color:#f92672">=</span> <span style="color:#ae81ff">998244353ll</span>;

<span style="color:#66d9ef">class</span> <span style="color:#a6e22e">NTT</span> {
	<span style="color:#66d9ef">private</span><span style="color:#f92672">:</span>
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">inline</span> i64 qpow(i64 a, i64 x, i64 m) {
			i64 ret <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>;
			<span style="color:#66d9ef">while</span>(x) { <span style="color:#66d9ef">if</span>(x <span style="color:#f92672">&amp;</span> <span style="color:#ae81ff">1</span>) ret <span style="color:#f92672">=</span> (ret<span style="color:#f92672">*</span>a) <span style="color:#f92672">%</span> m; a <span style="color:#f92672">=</span> (a<span style="color:#f92672">*</span>a) <span style="color:#f92672">%</span> m; x <span style="color:#f92672">&gt;&gt;=</span> <span style="color:#ae81ff">1</span>; }
			<span style="color:#66d9ef">return</span> ret;
		}
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">inline</span> i64 <span style="color:#a6e22e">to_pow2</span>(i64 x) {
			i64 ret <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; <span style="color:#66d9ef">while</span>(ret <span style="color:#f92672">&lt;</span> x) ret <span style="color:#f92672">&lt;&lt;=</span> <span style="color:#ae81ff">1</span>;
			<span style="color:#66d9ef">return</span> ret;
		}
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">inline</span> i64 <span style="color:#a6e22e">get_upper</span>(i64 x) {
			i64 a <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>, ret <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>; <span style="color:#66d9ef">while</span>(a <span style="color:#f92672">&lt;</span> x) { a <span style="color:#f92672">&lt;&lt;=</span> <span style="color:#ae81ff">1</span>; ret <span style="color:#f92672">++</span>; };
			<span style="color:#66d9ef">return</span> ret;
		}
		i64 m_wn[K<span style="color:#f92672">+</span><span style="color:#ae81ff">1</span>], m_iwn[K<span style="color:#f92672">+</span><span style="color:#ae81ff">1</span>], m_rev[N], m_inv, m_n <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>;
		<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">transform</span>(i64 <span style="color:#f92672">*</span>a, <span style="color:#66d9ef">const</span> i64 <span style="color:#f92672">*</span>wn) <span style="color:#66d9ef">const</span> {
			<span style="color:#66d9ef">for</span>(i64 i <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>; i <span style="color:#f92672">&lt;</span> m_n; <span style="color:#f92672">++</span>i) <span style="color:#66d9ef">if</span>(i <span style="color:#f92672">&lt;</span> m_rev[i]) std<span style="color:#f92672">::</span>swap(a[i], a[m_rev[i]]);

			<span style="color:#66d9ef">for</span>(i64 l <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; (<span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> l) <span style="color:#f92672">&lt;=</span> m_n; l <span style="color:#f92672">++</span>) {
				i64 m <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> (l<span style="color:#f92672">-</span><span style="color:#ae81ff">1</span>);
				<span style="color:#66d9ef">for</span>(i64 <span style="color:#f92672">*</span>p <span style="color:#f92672">=</span> a; p <span style="color:#f92672">!=</span> a <span style="color:#f92672">+</span> m_n; p <span style="color:#f92672">+=</span> (<span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> l)) {
					i64 w <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>;
					<span style="color:#66d9ef">for</span>(i64 i <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>; i <span style="color:#f92672">&lt;</span> m; <span style="color:#f92672">++</span>i) {
						i64 t <span style="color:#f92672">=</span> (w <span style="color:#f92672">*</span> p[m<span style="color:#f92672">+</span>i]) <span style="color:#f92672">%</span> P; p[m<span style="color:#f92672">+</span>i] <span style="color:#f92672">=</span> (p[i] <span style="color:#f92672">-</span> t <span style="color:#f92672">+</span> P) <span style="color:#f92672">%</span> P; p[i] <span style="color:#f92672">=</span> (p[i] <span style="color:#f92672">+</span> t) <span style="color:#f92672">%</span> P; w <span style="color:#f92672">=</span> (w <span style="color:#f92672">*</span> wn[l]) <span style="color:#f92672">%</span> P;
					}
				}
			}
		}

	<span style="color:#66d9ef">public</span><span style="color:#f92672">:</span>
		<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> Init() {
			<span style="color:#66d9ef">for</span>(i64 l <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; (<span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> l) <span style="color:#f92672">&lt;=</span> N; l <span style="color:#f92672">++</span>) {
				m_wn[l] <span style="color:#f92672">=</span> qpow(<span style="color:#ae81ff">3</span>, (P <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">/</span> (<span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> l), P);
				m_iwn[l] <span style="color:#f92672">=</span> qpow(<span style="color:#ae81ff">3</span>, P <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span> <span style="color:#f92672">-</span> (P <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">/</span> (<span style="color:#ae81ff">1</span> <span style="color:#f92672">&lt;&lt;</span> l), P);
			}
			m_rev[<span style="color:#ae81ff">0</span>] <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>; 
		}
		<span style="color:#66d9ef">inline</span> i64 <span style="color:#a6e22e">SetN</span>(i64 n) {
			n <span style="color:#f92672">=</span> to_pow2(n); <span style="color:#66d9ef">if</span>(n <span style="color:#f92672">==</span> m_n) <span style="color:#66d9ef">return</span> m_n;
			m_n <span style="color:#f92672">=</span> n;

			<span style="color:#66d9ef">int</span> k <span style="color:#f92672">=</span> get_upper(n);
			<span style="color:#66d9ef">for</span>(i64 i <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; i <span style="color:#f92672">&lt;</span> m_n; <span style="color:#f92672">++</span>i) m_rev[i] <span style="color:#f92672">=</span> (m_rev[i<span style="color:#f92672">&gt;&gt;</span><span style="color:#ae81ff">1</span>] <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">|</span> ( (i<span style="color:#f92672">&amp;</span><span style="color:#ae81ff">1</span>) <span style="color:#f92672">&lt;&lt;</span> (k <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>) );
			m_inv <span style="color:#f92672">=</span> qpow(m_n, P <span style="color:#f92672">-</span> <span style="color:#ae81ff">2</span>, P);
			<span style="color:#66d9ef">return</span> m_n;
		}
		<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">Dft</span>(i64 <span style="color:#f92672">*</span>a) <span style="color:#66d9ef">const</span> { transform(a, m_wn); }
