	<span style="color:#66d9ef">int</span> sort_tmp[N];
	Node <span style="color:#f92672">*</span><span style="color:#a6e22e">build_tree</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r)
	{
