			std<span style="color:#f92672">::</span>merge(sort_tmp <span style="color:#f92672">+</span> l, sort_tmp <span style="color:#f92672">+</span> mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, sort_tmp <span style="color:#f92672">+</span> mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, sort_tmp <span style="color:#f92672">+</span> r <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, current<span style="color:#f92672">-&gt;</span>vec.begin());
