</span><span style="color:#75715e"></span>			current<span style="color:#f92672">-&gt;</span>vec.push_back(arr[l]);
		}

		<span style="color:#66d9ef">return</span> current;
	}

	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">query_bound</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>rmin, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>rmax) <span style="color:#75715e">//should first initialize rmin and rmax with MAXV and MINV
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span>;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
		{
			<span style="color:#f92672">*</span>rmin <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>min(<span style="color:#f92672">*</span>rmin, node<span style="color:#f92672">-&gt;</span>rmin);
			<span style="color:#f92672">*</span>rmax <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>max(<span style="color:#f92672">*</span>rmax, node<span style="color:#f92672">-&gt;</span>rmax);
		}
		<span style="color:#66d9ef">else</span>
		{
			query_bound(node<span style="color:#f92672">-&gt;</span>lson, l, r, rmin, rmax);
			query_bound(node<span style="color:#f92672">-&gt;</span>rson, l, r, rmin, rmax);
		}
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_rank</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return number of value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> vec_query_rank(node<span style="color:#f92672">-&gt;</span>vec, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> query_rank(node<span style="color:#f92672">-&gt;</span>lson, l, r, x) <span style="color:#f92672">+</span> query_rank(node<span style="color:#f92672">-&gt;</span>rson, l, r, x);
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_kth</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> k) <span style="color:#75715e">//k &gt;= 1
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">int</span> rmin, rmax, low, high, mid, midrk;

		rmin <span style="color:#f92672">=</span> MAXV, rmax <span style="color:#f92672">=</span> MINV;
		query_bound(segroot, l, r, <span style="color:#f92672">&amp;</span>rmin, <span style="color:#f92672">&amp;</span>rmax);

		<span style="color:#75715e">//binary search
</span><span style="color:#75715e"></span>		low <span style="color:#f92672">=</span> rmin; high <span style="color:#f92672">=</span> rmax;
		<span style="color:#66d9ef">while</span>(low <span style="color:#f92672">!=</span> high)
		{
			mid <span style="color:#f92672">=</span> (low <span style="color:#f92672">+</span> high <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>; 
			midrk <span style="color:#f92672">=</span> query_rank(segroot, l, r, mid) <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>;
			<span style="color:#66d9ef">if</span>(midrk <span style="color:#f92672">&lt;=</span> k) low <span style="color:#f92672">=</span> mid;
			<span style="color:#66d9ef">else</span> high <span style="color:#f92672">=</span> mid <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>;
		}
		<span style="color:#66d9ef">return</span> low;
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_prev</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) 
	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> MINV;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> vec_query_prev(node<span style="color:#f92672">-&gt;</span>vec, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> std<span style="color:#f92672">::</span>max(query_prev(node<span style="color:#f92672">-&gt;</span>lson, l, r, x), query_prev(node<span style="color:#f92672">-&gt;</span>rson, l, r, x));
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_next</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) 
	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> MAXV;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> vec_query_next(node<span style="color:#f92672">-&gt;</span>vec, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> std<span style="color:#f92672">::</span>min(query_next(node<span style="color:#f92672">-&gt;</span>lson, l, r, x), query_next(node<span style="color:#f92672">-&gt;</span>rson, l, r, x));
	}

	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">modify</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> pos, <span style="color:#66d9ef">int</span> x, <span style="color:#66d9ef">int</span> old)
	{
		vec_modify(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>vec, x, old);

		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">!=</span> node<span style="color:#f92672">-&gt;</span>r)
		{
			<span style="color:#66d9ef">if</span>(pos <span style="color:#f92672">&lt;=</span> ((node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">+</span> node<span style="color:#f92672">-&gt;</span>r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>))
				modify(node<span style="color:#f92672">-&gt;</span>lson, pos, x, old);
			<span style="color:#66d9ef">else</span> modify(node<span style="color:#f92672">-&gt;</span>rson, pos, x, old);

			pushup(node);
		}
		<span style="color:#66d9ef">else</span>
			node<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> x;
	}
}

<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">main</span>()
{
	<span style="color:#66d9ef">int</span> n, m;
	scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>n); scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>m);
	<span style="color:#66d9ef">for</span>(<span style="color:#66d9ef">int</span> i <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; i <span style="color:#f92672">&lt;=</span> n; <span style="color:#f92672">++</span>i) scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, arr <span style="color:#f92672">+</span> i);
	seg<span style="color:#f92672">::</span>segroot <span style="color:#f92672">=</span> seg<span style="color:#f92672">::</span>build_tree(<span style="color:#ae81ff">1</span>, n);

	<span style="color:#66d9ef">int</span> opt, l, r, k, pos;
	<span style="color:#66d9ef">while</span>(m <span style="color:#f92672">--</span>)
	{
		scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>opt);
		<span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">1</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_rank(seg<span style="color:#f92672">::</span>segroot, l, r, k) <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>);
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">2</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_kth(l, r, k));
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">3</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>pos, <span style="color:#f92672">&amp;</span>k);
			seg<span style="color:#f92672">::</span>modify(seg<span style="color:#f92672">::</span>segroot, pos, k, arr[pos]);
			arr[pos] <span style="color:#f92672">=</span> k;
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">4</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_prev(seg<span style="color:#f92672">::</span>segroot, l, r, k));
		}
		<span style="color:#66d9ef">else</span>
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_next(seg<span style="color:#f92672">::</span>segroot, l, r, k));
		}
	}

	<span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
}
</code></pre></div><h2 id="splay版tle-70">splay版（TLE 70%）</h2>
<div class="highlight"><pre style="color:#f8f8f2;background-color:#272822;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-cpp" data-lang="cpp"><span style="color:#75715e">#include</span> <span style="color:#75715e">&lt;cstdio&gt;</span><span style="color:#75715e">
</span><span style="color:#75715e">#include</span> <span style="color:#75715e">&lt;algorithm&gt;</span><span style="color:#75715e">
</span><span style="color:#75715e"></span>
<span style="color:#75715e">#define N 50005
</span><span style="color:#75715e">#define MINV -2147483647
</span><span style="color:#75715e">#define MAXV 2147483647
</span><span style="color:#75715e"></span>
<span style="color:#66d9ef">int</span> arr[N];
<span style="color:#66d9ef">namespace</span> splay
{
	<span style="color:#66d9ef">struct</span> <span style="color:#a6e22e">Node</span>
	{
		Node <span style="color:#f92672">*</span>parent;
		<span style="color:#66d9ef">union</span> { <span style="color:#66d9ef">struct</span> {Node <span style="color:#f92672">*</span>lson, <span style="color:#f92672">*</span>rson; }; Node <span style="color:#f92672">*</span>son[<span style="color:#ae81ff">2</span>]; };
		<span style="color:#66d9ef">int</span> count, size, value;
	} node_pool[N <span style="color:#f92672">&lt;&lt;</span> <span style="color:#ae81ff">7</span>];

	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">new_node</span>() { <span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>p <span style="color:#f92672">=</span> node_pool; p<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> p<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> p<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> NULL; <span style="color:#66d9ef">return</span> p<span style="color:#f92672">++</span>; }
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">bool</span> <span style="color:#a6e22e">which_son</span>(Node <span style="color:#f92672">*</span>node) { <span style="color:#66d9ef">return</span> node<span style="color:#f92672">-&gt;</span>parent<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">==</span> node; }
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">pushup</span>(Node <span style="color:#f92672">*</span>node)
	{
		node<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>count;
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>lson) node<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">+=</span> node<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>size;
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>rson) node<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">+=</span> node<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>size;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">rotate</span>(Node <span style="color:#f92672">**</span>root, Node <span style="color:#f92672">*</span>node)
	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">bool</span> a, b; <span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>mov, <span style="color:#f92672">*</span>fa, <span style="color:#f92672">*</span>gfa;
		fa <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>parent;
		a <span style="color:#f92672">=</span> which_son(node); b <span style="color:#f92672">=</span> <span style="color:#f92672">!</span>a;
		mov <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>son[b]; fa<span style="color:#f92672">-&gt;</span>son[a] <span style="color:#f92672">=</span> mov; <span style="color:#66d9ef">if</span>(mov) mov<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> fa;

		gfa <span style="color:#f92672">=</span> fa<span style="color:#f92672">-&gt;</span>parent;
		(gfa <span style="color:#f92672">?</span> gfa<span style="color:#f92672">-&gt;</span>son[which_son(fa)] <span style="color:#f92672">:</span> <span style="color:#f92672">*</span>root) <span style="color:#f92672">=</span> node; node<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> gfa;

		node<span style="color:#f92672">-&gt;</span>son[b] <span style="color:#f92672">=</span> fa; fa<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> node;

		pushup(fa); pushup(node);
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">splay</span>(Node <span style="color:#f92672">**</span>root, Node <span style="color:#f92672">*</span>node, Node <span style="color:#f92672">*</span>target)
	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>fa, <span style="color:#f92672">*</span>gfa;
		<span style="color:#66d9ef">while</span>((fa <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>parent) <span style="color:#f92672">!=</span> target)
		{
			gfa <span style="color:#f92672">=</span> fa<span style="color:#f92672">-&gt;</span>parent;
			<span style="color:#66d9ef">if</span>(gfa <span style="color:#f92672">!=</span> target)
				rotate(root, which_son(node) <span style="color:#f92672">==</span> which_son(fa) <span style="color:#f92672">?</span> fa : node);
			rotate(root, node);
		}
	}
	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">print</span>(Node <span style="color:#f92672">*</span>node)
	{
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>node) <span style="color:#66d9ef">return</span>;
		print(node<span style="color:#f92672">-&gt;</span>lson);
		printf(<span style="color:#e6db74">&#34;%d %d; &#34;</span>, node<span style="color:#f92672">-&gt;</span>value, node<span style="color:#f92672">-&gt;</span>count);
		print(node<span style="color:#f92672">-&gt;</span>rson);
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">find</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x)
	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>current;
		current <span style="color:#f92672">=</span> <span style="color:#f92672">*</span>root;
		<span style="color:#66d9ef">while</span>(current <span style="color:#f92672">&amp;&amp;</span> current<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">!=</span> x)
			<span style="color:#66d9ef">if</span>(x <span style="color:#f92672">&lt;</span> current<span style="color:#f92672">-&gt;</span>value) current <span style="color:#f92672">=</span> current<span style="color:#f92672">-&gt;</span>lson;
			<span style="color:#66d9ef">else</span> current <span style="color:#f92672">=</span> current<span style="color:#f92672">-&gt;</span>rson;
		<span style="color:#66d9ef">if</span>(current) splay(root, current, NULL);
		<span style="color:#66d9ef">return</span> current;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">find_target</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x, Node <span style="color:#f92672">***</span>target, Node <span style="color:#f92672">**</span>father) 
		<span style="color:#75715e">//return pointer of node to be inserted and its parent
</span><span style="color:#75715e"></span>	{
		<span style="color:#f92672">*</span>target <span style="color:#f92672">=</span> root;
		<span style="color:#66d9ef">while</span>(<span style="color:#f92672">**</span>target)
		{
			<span style="color:#f92672">*</span>father <span style="color:#f92672">=</span> <span style="color:#f92672">**</span>target;
			<span style="color:#66d9ef">if</span>(x <span style="color:#f92672">&lt;</span> (<span style="color:#f92672">**</span>target)<span style="color:#f92672">-&gt;</span>value) <span style="color:#f92672">*</span>target <span style="color:#f92672">=</span> <span style="color:#f92672">&amp;</span>((<span style="color:#f92672">**</span>target)<span style="color:#f92672">-&gt;</span>lson);
			<span style="color:#66d9ef">else</span> <span style="color:#f92672">*</span>target <span style="color:#f92672">=</span> <span style="color:#f92672">&amp;</span>((<span style="color:#f92672">**</span>target)<span style="color:#f92672">-&gt;</span>rson);
		}
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">query_max</span>(Node <span style="color:#f92672">*</span>root) <span style="color:#75715e">//get max value in a tree
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">while</span>(root<span style="color:#f92672">-&gt;</span>rson)
			root <span style="color:#f92672">=</span> root<span style="color:#f92672">-&gt;</span>rson;
		<span style="color:#66d9ef">return</span> root;
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">query_min</span>(Node <span style="color:#f92672">*</span>root) <span style="color:#75715e">//get min value in a tree
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">while</span>(root<span style="color:#f92672">-&gt;</span>lson)
			root <span style="color:#f92672">=</span> root<span style="color:#f92672">-&gt;</span>lson;
		<span style="color:#66d9ef">return</span> root;
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">insert</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x)
	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>f;
		f <span style="color:#f92672">=</span> find(root, x);
		<span style="color:#66d9ef">if</span>(f) { f<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">++</span>; f<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">++</span>; }
		<span style="color:#66d9ef">else</span>
		{
			Node <span style="color:#f92672">**</span>target, <span style="color:#f92672">*</span>father;
			find_target(root, x, <span style="color:#f92672">&amp;</span>target, <span style="color:#f92672">&amp;</span>father);
			(<span style="color:#f92672">*</span>target) <span style="color:#f92672">=</span> new_node(); (<span style="color:#f92672">*</span>target)<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">=</span> x; 
			(<span style="color:#f92672">*</span>target)<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">=</span> (<span style="color:#f92672">*</span>target)<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; (<span style="color:#f92672">*</span>target)<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> father;
			splay(root, <span style="color:#f92672">*</span>target, NULL);
		}
		<span style="color:#66d9ef">return</span> <span style="color:#f92672">*</span>root;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">erase</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x)
	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>f, <span style="color:#f92672">*</span>m;
		f <span style="color:#f92672">=</span> find(root, x);
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>f) <span style="color:#66d9ef">return</span>;
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">--</span>f<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">&amp;&amp;</span> <span style="color:#f92672">--</span>f<span style="color:#f92672">-&gt;</span>size) <span style="color:#66d9ef">return</span>;
		<span style="color:#75715e">//f is the root now
</span><span style="color:#75715e"></span>		<span style="color:#66d9ef">if</span>(f<span style="color:#f92672">-&gt;</span>lson)
		{
			m <span style="color:#f92672">=</span> query_max(f<span style="color:#f92672">-&gt;</span>lson);
			splay(root, m, f);
			m<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> NULL; 
			m<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> f<span style="color:#f92672">-&gt;</span>rson; <span style="color:#66d9ef">if</span>(f<span style="color:#f92672">-&gt;</span>rson) { m<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">+=</span> f<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>size; f<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> m; }
			<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> m;
		}
		<span style="color:#66d9ef">else</span> <span style="color:#75715e">//left son is empty, let rson be the root
</span><span style="color:#75715e"></span>		{
			<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> (<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>rson;
			(<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> NULL;
		}
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_rank</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return number of value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">bool</span> exist;
		exist <span style="color:#f92672">=</span> find(root, x);
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>exist) insert(root, x);
		<span style="color:#66d9ef">int</span> val <span style="color:#f92672">=</span> (<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">?</span> (<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>size : <span style="color:#ae81ff">0</span>;
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>exist) erase(root, x);
		<span style="color:#66d9ef">return</span> val;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_prev</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return the biggest value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">bool</span> exist;
		exist <span style="color:#f92672">=</span> find(root, x);
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>exist) insert(root, x); <span style="color:#75715e">//the new node is the root
</span><span style="color:#75715e"></span>		<span style="color:#66d9ef">int</span> val <span style="color:#f92672">=</span> (<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">?</span> query_max((<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>lson)<span style="color:#f92672">-&gt;</span>value : MINV;
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>exist) erase(root, x); <span style="color:#75715e">//the new node is the root
</span><span style="color:#75715e"></span>		<span style="color:#66d9ef">return</span> val;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_next</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x)
	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">bool</span> exist;
		exist <span style="color:#f92672">=</span> find(root, x);
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>exist) insert(root, x);
		<span style="color:#66d9ef">int</span> val <span style="color:#f92672">=</span> (<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">?</span> query_min((<span style="color:#f92672">*</span>root)<span style="color:#f92672">-&gt;</span>rson)<span style="color:#f92672">-&gt;</span>value : MAXV;
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>exist) erase(root, x);
		<span style="color:#66d9ef">return</span> val;
	}
	Node <span style="color:#f92672">*</span><span style="color:#a6e22e">build_tree</span>(<span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>value_arr, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>cnt_arr, Node <span style="color:#f92672">*</span>father, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r)
	{
		<span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> NULL;

		<span style="color:#66d9ef">int</span> mid <span style="color:#f92672">=</span> (l <span style="color:#f92672">+</span> r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>;
		Node <span style="color:#f92672">*</span>current <span style="color:#f92672">=</span> new_node(); 
		current<span style="color:#f92672">-&gt;</span>parent <span style="color:#f92672">=</span> father;
		current<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">=</span> value_arr[mid];
		current<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">=</span> cnt_arr[mid];
		current<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> build_tree(value_arr, cnt_arr, current, l, mid <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>);
		current<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> build_tree(value_arr, cnt_arr, current, mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, r);

		pushup(current);

		<span style="color:#66d9ef">return</span> current;
	}
}

<span style="color:#66d9ef">namespace</span> seg
{
	<span style="color:#66d9ef">struct</span> <span style="color:#a6e22e">Node</span>
	{
		splay<span style="color:#f92672">::</span>Node <span style="color:#f92672">*</span>root;
		Node <span style="color:#f92672">*</span>lson, <span style="color:#f92672">*</span>rson;
		<span style="color:#66d9ef">int</span> l, r, rmin, rmax;
	};

	Node node_pool[N <span style="color:#f92672">&lt;&lt;</span> <span style="color:#ae81ff">2</span>], <span style="color:#f92672">*</span>segroot;

	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">new_node</span>() { <span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>p <span style="color:#f92672">=</span> node_pool; p<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> p<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> NULL; <span style="color:#66d9ef">return</span> p<span style="color:#f92672">++</span>; }

	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">pushup</span>(Node <span style="color:#f92672">*</span>node)
	{
		node<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>max(node<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>rmax : MINV, node<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>rmax : MINV);
		node<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>min(node<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>rmin : MAXV, node<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>rmin : MAXV);
	}
	<span style="color:#66d9ef">int</span> sort_tmp[N], merge_tmp[N], cnt_tmp[N];
	Node <span style="color:#f92672">*</span><span style="color:#a6e22e">build_tree</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r)
	{
		Node <span style="color:#f92672">*</span>current; <span style="color:#66d9ef">int</span> mid, sz, total;

		current <span style="color:#f92672">=</span> new_node();
		current<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">=</span> l; 
		current<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">=</span> r;

		<span style="color:#66d9ef">if</span>(l <span style="color:#f92672">!=</span> r)
		{
			mid <span style="color:#f92672">=</span> (l <span style="color:#f92672">+</span> r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>;
			current<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> build_tree(l, mid);
			current<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> build_tree(mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, r);
			pushup(current);

			sz <span style="color:#f92672">=</span> r<span style="color:#f92672">-</span>l<span style="color:#f92672">+</span><span style="color:#ae81ff">1</span>;

			<span style="color:#75715e">//merge sort
</span><span style="color:#75715e"></span>			std<span style="color:#f92672">::</span>merge(sort_tmp <span style="color:#f92672">+</span> l, sort_tmp <span style="color:#f92672">+</span> mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, sort_tmp <span style="color:#f92672">+</span> mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, sort_tmp <span style="color:#f92672">+</span> r <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, merge_tmp <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>);
			std<span style="color:#f92672">::</span>copy(merge_tmp <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, merge_tmp <span style="color:#f92672">+</span> sz<span style="color:#f92672">+</span><span style="color:#ae81ff">1</span>, sort_tmp <span style="color:#f92672">+</span> l);

			total <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>;
			<span style="color:#66d9ef">for</span>(<span style="color:#66d9ef">int</span> i <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; i <span style="color:#f92672">&lt;=</span> sz; <span style="color:#f92672">++</span>i) <span style="color:#75715e">//make the array(for building tree) unique and get the count
</span><span style="color:#75715e"></span>				<span style="color:#66d9ef">if</span>(i <span style="color:#f92672">==</span> <span style="color:#ae81ff">1</span> <span style="color:#f92672">||</span> merge_tmp[i] <span style="color:#f92672">!=</span> merge_tmp[i<span style="color:#f92672">-</span><span style="color:#ae81ff">1</span>])
				{
					<span style="color:#f92672">++</span>total;
					merge_tmp[total] <span style="color:#f92672">=</span> merge_tmp[i];
					cnt_tmp[total] <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>;
				}
				<span style="color:#66d9ef">else</span> cnt_tmp[total] <span style="color:#f92672">++</span>;

			<span style="color:#75715e">//build balanced tree
</span><span style="color:#75715e"></span>			current<span style="color:#f92672">-&gt;</span>root <span style="color:#f92672">=</span> splay<span style="color:#f92672">::</span>build_tree(merge_tmp, cnt_tmp, NULL, <span style="color:#ae81ff">1</span>, total);
		}
		<span style="color:#66d9ef">else</span>
		{
			sort_tmp[l] <span style="color:#f92672">=</span> arr[l];
			current<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> current<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> arr[l];

			<span style="color:#75715e">//create tree with 1 element
</span><span style="color:#75715e"></span>			current<span style="color:#f92672">-&gt;</span>root <span style="color:#f92672">=</span> splay<span style="color:#f92672">::</span>new_node();
			current<span style="color:#f92672">-&gt;</span>root<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">=</span> arr[l];
			current<span style="color:#f92672">-&gt;</span>root<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> current<span style="color:#f92672">-&gt;</span>root<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>;
		}

		<span style="color:#66d9ef">return</span> current;
	}

	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">query_bound</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>rmin, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>rmax) <span style="color:#75715e">//should first initialize rmin and rmax with MAXV and MINV
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span>;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
		{
			<span style="color:#f92672">*</span>rmin <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>min(<span style="color:#f92672">*</span>rmin, node<span style="color:#f92672">-&gt;</span>rmin);
			<span style="color:#f92672">*</span>rmax <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>max(<span style="color:#f92672">*</span>rmax, node<span style="color:#f92672">-&gt;</span>rmax);
		}
		<span style="color:#66d9ef">else</span>
		{
			query_bound(node<span style="color:#f92672">-&gt;</span>lson, l, r, rmin, rmax);
			query_bound(node<span style="color:#f92672">-&gt;</span>rson, l, r, rmin, rmax);
		}
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_rank</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return number of value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> splay<span style="color:#f92672">::</span>query_rank(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> query_rank(node<span style="color:#f92672">-&gt;</span>lson, l, r, x) <span style="color:#f92672">+</span> query_rank(node<span style="color:#f92672">-&gt;</span>rson, l, r, x);
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_kth</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> k) <span style="color:#75715e">//k &gt;= 1
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">int</span> rmin, rmax, low, high, mid, midrk;

		rmin <span style="color:#f92672">=</span> MAXV, rmax <span style="color:#f92672">=</span> MINV;
		query_bound(segroot, l, r, <span style="color:#f92672">&amp;</span>rmin, <span style="color:#f92672">&amp;</span>rmax);

		<span style="color:#75715e">//binary search
</span><span style="color:#75715e"></span>		low <span style="color:#f92672">=</span> rmin; high <span style="color:#f92672">=</span> rmax;
		<span style="color:#66d9ef">while</span>(low <span style="color:#f92672">!=</span> high)
		{
			mid <span style="color:#f92672">=</span> (low <span style="color:#f92672">+</span> high <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>; 
			midrk <span style="color:#f92672">=</span> query_rank(segroot, l, r, mid) <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>;
			<span style="color:#66d9ef">if</span>(midrk <span style="color:#f92672">&lt;=</span> k) low <span style="color:#f92672">=</span> mid;
			<span style="color:#66d9ef">else</span> high <span style="color:#f92672">=</span> mid <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>;
		}
		<span style="color:#66d9ef">return</span> low;
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_prev</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) 
	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> MINV;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> splay<span style="color:#f92672">::</span>query_prev(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> std<span style="color:#f92672">::</span>max(query_prev(node<span style="color:#f92672">-&gt;</span>lson, l, r, x), query_prev(node<span style="color:#f92672">-&gt;</span>rson, l, r, x));
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_next</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) 
	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> MAXV;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> splay<span style="color:#f92672">::</span>query_next(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> std<span style="color:#f92672">::</span>min(query_next(node<span style="color:#f92672">-&gt;</span>lson, l, r, x), query_next(node<span style="color:#f92672">-&gt;</span>rson, l, r, x));
	}

	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">modify</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> pos, <span style="color:#66d9ef">int</span> x, <span style="color:#66d9ef">int</span> old)
	{
		splay<span style="color:#f92672">::</span>insert(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		splay<span style="color:#f92672">::</span>erase(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, old);

		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">!=</span> node<span style="color:#f92672">-&gt;</span>r)
		{
			<span style="color:#66d9ef">if</span>(pos <span style="color:#f92672">&lt;=</span> ((node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">+</span> node<span style="color:#f92672">-&gt;</span>r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>))
				modify(node<span style="color:#f92672">-&gt;</span>lson, pos, x, old);
			<span style="color:#66d9ef">else</span> modify(node<span style="color:#f92672">-&gt;</span>rson, pos, x, old);

			pushup(node);
		}
		<span style="color:#66d9ef">else</span>
			node<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> x;
	}
}

<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">main</span>()
{
	<span style="color:#66d9ef">int</span> n, m;
	scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>n); scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>m);
	<span style="color:#66d9ef">for</span>(<span style="color:#66d9ef">int</span> i <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; i <span style="color:#f92672">&lt;=</span> n; <span style="color:#f92672">++</span>i) scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, arr <span style="color:#f92672">+</span> i);
	seg<span style="color:#f92672">::</span>segroot <span style="color:#f92672">=</span> seg<span style="color:#f92672">::</span>build_tree(<span style="color:#ae81ff">1</span>, n);

	<span style="color:#66d9ef">int</span> opt, l, r, k, pos;
	<span style="color:#66d9ef">while</span>(m <span style="color:#f92672">--</span>)
	{
		scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>opt);
		<span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">1</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_rank(seg<span style="color:#f92672">::</span>segroot, l, r, k) <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>);
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">2</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_kth(l, r, k));
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">3</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>pos, <span style="color:#f92672">&amp;</span>k);
			seg<span style="color:#f92672">::</span>modify(seg<span style="color:#f92672">::</span>segroot, pos, k, arr[pos]);
			arr[pos] <span style="color:#f92672">=</span> k;
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">4</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_prev(seg<span style="color:#f92672">::</span>segroot, l, r, k));
		}
		<span style="color:#66d9ef">else</span>
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_next(seg<span style="color:#f92672">::</span>segroot, l, r, k));
		}
	}

	<span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
}
