	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">new_node</span>() { <span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>p <span style="color:#f92672">=</span> node_pool; p<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> p<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> NULL; p<span style="color:#f92672">-&gt;</span>rndkey <span style="color:#f92672">=</span> gen(); <span style="color:#66d9ef">return</span> p<span style="color:#f92672">++</span>; }
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">maintain</span>(Node <span style="color:#f92672">*</span>node)
	{
		node<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>count;
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>lson) node<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">+=</span> node<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>size;
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>rson) node<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">+=</span> node<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>size;
	}
	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">print</span>(Node <span style="color:#f92672">*</span>node)
	{
		<span style="color:#66d9ef">if</span>(<span style="color:#f92672">!</span>node) <span style="color:#66d9ef">return</span>;
		print(node<span style="color:#f92672">-&gt;</span>lson);
		printf(<span style="color:#e6db74">&#34;%d %d; &#34;</span>, node<span style="color:#f92672">-&gt;</span>value, node<span style="color:#f92672">-&gt;</span>count);
		print(node<span style="color:#f92672">-&gt;</span>rson);
	}
	Node <span style="color:#f92672">*</span><span style="color:#a6e22e">init</span>()
	{
		Node <span style="color:#f92672">*</span>l <span style="color:#f92672">=</span> new_node(); l<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> l<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>; l<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">=</span> MINV;
		Node <span style="color:#f92672">*</span>r <span style="color:#f92672">=</span> new_node(); r<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> r<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">=</span> <span style="color:#ae81ff">0</span>; r<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">=</span> MAXV;
		<span style="color:#66d9ef">if</span>(l<span style="color:#f92672">-&gt;</span>rndkey <span style="color:#f92672">&lt;</span> r<span style="color:#f92672">-&gt;</span>rndkey) { l<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> r; <span style="color:#66d9ef">return</span> l; } <span style="color:#75715e">//keep l as root
</span><span style="color:#75715e"></span>		r<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> l; <span style="color:#66d9ef">return</span> r;
	}
	Node <span style="color:#f92672">*</span><span style="color:#a6e22e">merge</span>(Node <span style="color:#f92672">*</span>l, Node <span style="color:#f92672">*</span>r)
	{
		<span style="color:#66d9ef">if</span>(l <span style="color:#f92672">==</span> NULL) <span style="color:#66d9ef">return</span> r;
		<span style="color:#66d9ef">if</span>(r <span style="color:#f92672">==</span> NULL) <span style="color:#66d9ef">return</span> l;
		<span style="color:#66d9ef">if</span>(l<span style="color:#f92672">-&gt;</span>rndkey <span style="color:#f92672">&lt;</span> r<span style="color:#f92672">-&gt;</span>rndkey) <span style="color:#75715e">//keep l as root
</span><span style="color:#75715e"></span>		{
			l<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> merge(l<span style="color:#f92672">-&gt;</span>rson, r);
			maintain(l); <span style="color:#66d9ef">return</span> l;
		}
		r<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> merge(l, r<span style="color:#f92672">-&gt;</span>lson);
		maintain(r); <span style="color:#66d9ef">return</span> r;
	}
	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">split</span>(Node <span style="color:#f92672">*</span>root, <span style="color:#66d9ef">int</span> x, Node <span style="color:#f92672">**</span>l, Node <span style="color:#f92672">**</span>r)
	{
		<span style="color:#66d9ef">if</span>(root <span style="color:#f92672">==</span> NULL) { <span style="color:#f92672">*</span>l <span style="color:#f92672">=</span> <span style="color:#f92672">*</span>r <span style="color:#f92672">=</span> <span style="color:#66d9ef">nullptr</span>; <span style="color:#66d9ef">return</span>; }
		<span style="color:#66d9ef">if</span>(root<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">&lt;</span> x)
		{
			<span style="color:#f92672">*</span>l <span style="color:#f92672">=</span> root;
			split(root<span style="color:#f92672">-&gt;</span>rson, x, <span style="color:#f92672">&amp;</span>(root<span style="color:#f92672">-&gt;</span>rson), r);
		}
		<span style="color:#66d9ef">else</span>
		{
			<span style="color:#f92672">*</span>r <span style="color:#f92672">=</span> root;
			split(root<span style="color:#f92672">-&gt;</span>lson, x, l, <span style="color:#f92672">&amp;</span>(root<span style="color:#f92672">-&gt;</span>lson));
		}
		maintain(root);
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">find</span>(Node <span style="color:#f92672">*</span>root, <span style="color:#66d9ef">int</span> x)
	{
		<span style="color:#66d9ef">while</span>(root <span style="color:#f92672">&amp;&amp;</span> root<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">!=</span> x)
			<span style="color:#66d9ef">if</span>(x <span style="color:#f92672">&lt;</span> root<span style="color:#f92672">-&gt;</span>value) root <span style="color:#f92672">=</span> root<span style="color:#f92672">-&gt;</span>lson;
			<span style="color:#66d9ef">else</span> root <span style="color:#f92672">=</span> root<span style="color:#f92672">-&gt;</span>rson;
		<span style="color:#66d9ef">return</span> root;
	}
	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">pushup_size</span>(Node <span style="color:#f92672">*</span>root, <span style="color:#66d9ef">int</span> x, <span style="color:#66d9ef">int</span> plus) <span style="color:#75715e">//ensure find(root, x)
</span><span style="color:#75715e"></span>	{
		root<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">+=</span> plus;
		<span style="color:#66d9ef">if</span>(root<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">!=</span> x)
		{
			<span style="color:#66d9ef">if</span>(x <span style="color:#f92672">&lt;</span> root<span style="color:#f92672">-&gt;</span>value) pushup_size(root<span style="color:#f92672">-&gt;</span>lson, x, plus);
			<span style="color:#66d9ef">else</span> pushup_size(root<span style="color:#f92672">-&gt;</span>rson, x, plus);
		}
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">query_max</span>(Node <span style="color:#f92672">*</span>root) <span style="color:#75715e">//get max value in a tree
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">while</span>(root<span style="color:#f92672">-&gt;</span>rson)
			root <span style="color:#f92672">=</span> root<span style="color:#f92672">-&gt;</span>rson;
		<span style="color:#66d9ef">return</span> root;
	}
	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">query_min</span>(Node <span style="color:#f92672">*</span>root) <span style="color:#75715e">//get min value in a tree
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">while</span>(root<span style="color:#f92672">-&gt;</span>lson)
			root <span style="color:#f92672">=</span> root<span style="color:#f92672">-&gt;</span>lson;
		<span style="color:#66d9ef">return</span> root;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">insert</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return root
</span><span style="color:#75715e"></span>	{
		Node <span style="color:#f92672">*</span>f <span style="color:#f92672">=</span> find(<span style="color:#f92672">*</span>root, x);
		<span style="color:#66d9ef">if</span>(f)
		{
			f<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">++</span>;
			pushup_size(<span style="color:#f92672">*</span>root, x, <span style="color:#ae81ff">1</span>);
		}
		<span style="color:#66d9ef">else</span>
		{
			Node <span style="color:#f92672">*</span>l, <span style="color:#f92672">*</span>r;
			split(<span style="color:#f92672">*</span>root, x, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r); <span style="color:#75715e">//[MINV, x) and [x, MAXV]
</span><span style="color:#75715e"></span>			Node <span style="color:#f92672">*</span>ins <span style="color:#f92672">=</span> new_node();
			ins<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">=</span> ins<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>;
			ins<span style="color:#f92672">-&gt;</span>value <span style="color:#f92672">=</span> x;

			<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> merge(merge(l, ins), r);
		}
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">erase</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x)
	{
		Node <span style="color:#f92672">*</span>f <span style="color:#f92672">=</span> find(<span style="color:#f92672">*</span>root, x);
		f<span style="color:#f92672">-&gt;</span>count <span style="color:#f92672">--</span>;
		pushup_size(<span style="color:#f92672">*</span>root, x, <span style="color:#f92672">-</span><span style="color:#ae81ff">1</span>);

		<span style="color:#66d9ef">if</span>(f<span style="color:#f92672">-&gt;</span>count) <span style="color:#66d9ef">return</span>;

		Node <span style="color:#f92672">*</span>l, <span style="color:#f92672">*</span>r, <span style="color:#f92672">*</span>rl, <span style="color:#f92672">*</span>rr;
		split(<span style="color:#f92672">*</span>root, x, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r); <span style="color:#75715e">//[MINV, x)
</span><span style="color:#75715e"></span>		split(r, x <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, <span style="color:#f92672">&amp;</span>rl, <span style="color:#f92672">&amp;</span>rr);

		<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> merge(l, rr);
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_rank</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return number of value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>l, <span style="color:#f92672">*</span>r;
		split(<span style="color:#f92672">*</span>root, x, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r);
		<span style="color:#66d9ef">int</span> val <span style="color:#f92672">=</span> l<span style="color:#f92672">-&gt;</span>size;
		<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> merge(l, r);
		<span style="color:#66d9ef">return</span> val;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_prev</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return the biggest value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>l, <span style="color:#f92672">*</span>r;
		split(<span style="color:#f92672">*</span>root, x, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r);
		<span style="color:#66d9ef">int</span> val <span style="color:#f92672">=</span> query_max(l)<span style="color:#f92672">-&gt;</span>value;
		<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> merge(l, r);
		<span style="color:#66d9ef">return</span> val;
	}
	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_next</span>(Node <span style="color:#f92672">**</span>root, <span style="color:#66d9ef">int</span> x)
	{
		<span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>l, <span style="color:#f92672">*</span>r;
		split(<span style="color:#f92672">*</span>root, x <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r);
		<span style="color:#66d9ef">int</span> val <span style="color:#f92672">=</span> query_min(r)<span style="color:#f92672">-&gt;</span>value;
		<span style="color:#f92672">*</span>root <span style="color:#f92672">=</span> merge(l, r);
		<span style="color:#66d9ef">return</span> val;
	}
}

<span style="color:#66d9ef">namespace</span> seg
{
	<span style="color:#66d9ef">struct</span> <span style="color:#a6e22e">Node</span>
	{
		treap<span style="color:#f92672">::</span>Node <span style="color:#f92672">*</span>root;
		Node <span style="color:#f92672">*</span>lson, <span style="color:#f92672">*</span>rson;
		<span style="color:#66d9ef">int</span> l, r, rmin, rmax;
	};

	Node node_pool[N <span style="color:#f92672">&lt;&lt;</span> <span style="color:#ae81ff">2</span>], <span style="color:#f92672">*</span>segroot;

	<span style="color:#66d9ef">inline</span> Node <span style="color:#f92672">*</span><span style="color:#a6e22e">new_node</span>() { <span style="color:#66d9ef">static</span> Node <span style="color:#f92672">*</span>p <span style="color:#f92672">=</span> node_pool; p<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> p<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> NULL; <span style="color:#66d9ef">return</span> p<span style="color:#f92672">++</span>; }

	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">pushup</span>(Node <span style="color:#f92672">*</span>node)
	{
		node<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>max(node<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>rmax : MINV, node<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>rmax : MINV);
		node<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>min(node<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>lson<span style="color:#f92672">-&gt;</span>rmin : MAXV, node<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">?</span> node<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>rmin : MAXV);
	}

	Node <span style="color:#f92672">*</span><span style="color:#a6e22e">build_empty_tree</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r)
	{
		Node <span style="color:#f92672">*</span>current;

		current <span style="color:#f92672">=</span> new_node();
		current<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">=</span> l; 
		current<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">=</span> r;
		<span style="color:#75715e">//create tree with 2 element (MAXV &amp; MINV)
</span><span style="color:#75715e"></span>		current<span style="color:#f92672">-&gt;</span>root <span style="color:#f92672">=</span> treap<span style="color:#f92672">::</span>init();

		<span style="color:#66d9ef">if</span>(l <span style="color:#f92672">!=</span> r)
		{
			<span style="color:#66d9ef">int</span> mid <span style="color:#f92672">=</span> (l <span style="color:#f92672">+</span> r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>;
			current<span style="color:#f92672">-&gt;</span>lson <span style="color:#f92672">=</span> build_empty_tree(l, mid);
			current<span style="color:#f92672">-&gt;</span>rson <span style="color:#f92672">=</span> build_empty_tree(mid <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>, r);
			pushup(current);
		}
		<span style="color:#66d9ef">else</span>
			current<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> current<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> arr[l];

		<span style="color:#66d9ef">return</span> current;
	}

	<span style="color:#66d9ef">inline</span> <span style="color:#66d9ef">void</span> <span style="color:#a6e22e">build_tree</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r)
	{
		segroot <span style="color:#f92672">=</span> build_empty_tree(l, r);

		Node <span style="color:#f92672">*</span>current;
		<span style="color:#66d9ef">for</span>(<span style="color:#66d9ef">int</span> i <span style="color:#f92672">=</span> l; i <span style="color:#f92672">&lt;=</span> r; <span style="color:#f92672">++</span>i)
		{
			current <span style="color:#f92672">=</span> segroot;
			<span style="color:#66d9ef">while</span>(current)
			{
				treap<span style="color:#f92672">::</span>insert(<span style="color:#f92672">&amp;</span>current<span style="color:#f92672">-&gt;</span>root, arr[i]);
				current <span style="color:#f92672">=</span> i <span style="color:#f92672">&lt;=</span> ((current<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">+</span> current<span style="color:#f92672">-&gt;</span>r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">?</span> current<span style="color:#f92672">-&gt;</span>lson : current<span style="color:#f92672">-&gt;</span>rson;
			}
		}
	}

	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">query_bound</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>rmin, <span style="color:#66d9ef">int</span> <span style="color:#f92672">*</span>rmax) <span style="color:#75715e">//should first initialize rmin and rmax with MAXV and MINV
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span>;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
		{
			<span style="color:#f92672">*</span>rmin <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>min(<span style="color:#f92672">*</span>rmin, node<span style="color:#f92672">-&gt;</span>rmin);
			<span style="color:#f92672">*</span>rmax <span style="color:#f92672">=</span> std<span style="color:#f92672">::</span>max(<span style="color:#f92672">*</span>rmax, node<span style="color:#f92672">-&gt;</span>rmax);
		}
		<span style="color:#66d9ef">else</span>
		{
			query_bound(node<span style="color:#f92672">-&gt;</span>lson, l, r, rmin, rmax);
			query_bound(node<span style="color:#f92672">-&gt;</span>rson, l, r, rmin, rmax);
		}
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_rank</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) <span style="color:#75715e">//return number of value smaller than x
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> treap<span style="color:#f92672">::</span>query_rank(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> query_rank(node<span style="color:#f92672">-&gt;</span>lson, l, r, x) <span style="color:#f92672">+</span> query_rank(node<span style="color:#f92672">-&gt;</span>rson, l, r, x);
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_kth</span>(<span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> k) <span style="color:#75715e">//k &gt;= 1
</span><span style="color:#75715e"></span>	{
		<span style="color:#66d9ef">static</span> <span style="color:#66d9ef">int</span> rmin, rmax, low, high, mid, midrk;

		rmin <span style="color:#f92672">=</span> MAXV, rmax <span style="color:#f92672">=</span> MINV;
		query_bound(segroot, l, r, <span style="color:#f92672">&amp;</span>rmin, <span style="color:#f92672">&amp;</span>rmax);

		<span style="color:#75715e">//binary search
</span><span style="color:#75715e"></span>		low <span style="color:#f92672">=</span> rmin; high <span style="color:#f92672">=</span> rmax;
		<span style="color:#66d9ef">while</span>(low <span style="color:#f92672">!=</span> high)
		{
			mid <span style="color:#f92672">=</span> (low <span style="color:#f92672">+</span> high <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>; 
			midrk <span style="color:#f92672">=</span> query_rank(segroot, l, r, mid) <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>;
			<span style="color:#66d9ef">if</span>(midrk <span style="color:#f92672">&lt;=</span> k) low <span style="color:#f92672">=</span> mid;
			<span style="color:#66d9ef">else</span> high <span style="color:#f92672">=</span> mid <span style="color:#f92672">-</span> <span style="color:#ae81ff">1</span>;
		}
		<span style="color:#66d9ef">return</span> low;
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_prev</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) 
	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> MINV;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> treap<span style="color:#f92672">::</span>query_prev(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> std<span style="color:#f92672">::</span>max(query_prev(node<span style="color:#f92672">-&gt;</span>lson, l, r, x), query_prev(node<span style="color:#f92672">-&gt;</span>rson, l, r, x));
	}

	<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">query_next</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> l, <span style="color:#66d9ef">int</span> r, <span style="color:#66d9ef">int</span> x) 
	{
		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;</span> l <span style="color:#f92672">||</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&gt;</span> r) <span style="color:#66d9ef">return</span> MAXV;
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(l <span style="color:#f92672">&lt;=</span> node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">&amp;&amp;</span> node<span style="color:#f92672">-&gt;</span>r <span style="color:#f92672">&lt;=</span> r)
			<span style="color:#66d9ef">return</span> treap<span style="color:#f92672">::</span>query_next(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		<span style="color:#66d9ef">else</span>
			<span style="color:#66d9ef">return</span> std<span style="color:#f92672">::</span>min(query_next(node<span style="color:#f92672">-&gt;</span>lson, l, r, x), query_next(node<span style="color:#f92672">-&gt;</span>rson, l, r, x));
	}

	<span style="color:#66d9ef">void</span> <span style="color:#a6e22e">modify</span>(Node <span style="color:#f92672">*</span>node, <span style="color:#66d9ef">int</span> pos, <span style="color:#66d9ef">int</span> x, <span style="color:#66d9ef">int</span> old)
	{
		treap<span style="color:#f92672">::</span>insert(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, x);
		treap<span style="color:#f92672">::</span>erase(<span style="color:#f92672">&amp;</span>node<span style="color:#f92672">-&gt;</span>root, old);

		<span style="color:#66d9ef">if</span>(node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">!=</span> node<span style="color:#f92672">-&gt;</span>r)
		{
			<span style="color:#66d9ef">if</span>(pos <span style="color:#f92672">&lt;=</span> ((node<span style="color:#f92672">-&gt;</span>l <span style="color:#f92672">+</span> node<span style="color:#f92672">-&gt;</span>r) <span style="color:#f92672">&gt;&gt;</span> <span style="color:#ae81ff">1</span>))
				modify(node<span style="color:#f92672">-&gt;</span>lson, pos, x, old);
			<span style="color:#66d9ef">else</span> modify(node<span style="color:#f92672">-&gt;</span>rson, pos, x, old);

			pushup(node);
		}
		<span style="color:#66d9ef">else</span>
			node<span style="color:#f92672">-&gt;</span>rmax <span style="color:#f92672">=</span> node<span style="color:#f92672">-&gt;</span>rmin <span style="color:#f92672">=</span> x;
	}
}

<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">main</span>()
{
	<span style="color:#66d9ef">int</span> n, m;
	scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>n); scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>m);
	<span style="color:#66d9ef">for</span>(<span style="color:#66d9ef">int</span> i <span style="color:#f92672">=</span> <span style="color:#ae81ff">1</span>; i <span style="color:#f92672">&lt;=</span> n; <span style="color:#f92672">++</span>i) scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, arr <span style="color:#f92672">+</span> i);
	seg<span style="color:#f92672">::</span>build_tree(<span style="color:#ae81ff">1</span>, n);

	<span style="color:#66d9ef">int</span> opt, l, r, k, pos;
	<span style="color:#66d9ef">while</span>(m <span style="color:#f92672">--</span>)
	{
		scanf(<span style="color:#e6db74">&#34;%d&#34;</span>, <span style="color:#f92672">&amp;</span>opt);
		<span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">1</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_rank(seg<span style="color:#f92672">::</span>segroot, l, r, k) <span style="color:#f92672">+</span> <span style="color:#ae81ff">1</span>);
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">2</span>)
		{
			scanf(<span style="color:#e6db74">&#34;%d%d%d&#34;</span>, <span style="color:#f92672">&amp;</span>l, <span style="color:#f92672">&amp;</span>r, <span style="color:#f92672">&amp;</span>k);
			printf(<span style="color:#e6db74">&#34;%d</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>, seg<span style="color:#f92672">::</span>query_kth(l, r, k));
		}
		<span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(opt <span style="color:#f92672">==</span> <span style="color:#ae81ff">3</span>)
		{
