			root<span style="color:#f92672">-&gt;</span>rson<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">-=</span> len; root<span style="color:#f92672">-&gt;</span>size <span style="color:#f92672">-=</span> len;
		}
